mapper.select:
select count(1) count
from attendance_classes t
where 1 =1
and t.status_cd= '0'

    and t.classes_id= #{classesId}


    and t.create_time= #{createTime}

select t.classes_id classesId,t.create_time createTime,t.classes_name classesName,t.status_cd statusCd,t.time_offset timeOffset,t.clock_count clockCount,t.clock_type clockType,t.clock_type_value clockTypeValue, t.late_offset lateOffset,t.leave_offset leaveOffset from attendance_classes t where 1 =1 and t.status_cd= '0'
and t.classes_name like '%${classesName}%'
and t.classes_id= #{classesId}
and t.create_time= #{createTime}
order by t.create_time desc
limit #{page}, #{row}

mapper.update:
update attendance_classes t set t.status_cd = #{statusCd}

    , t.classes_name= #{classesName}


    , t.time_offset= #{timeOffset}


    , t.clock_count= #{clockCount}


    , t.clock_type_value= #{clockTypeValue}


    , t.late_offset= #{lateOffset}


    , t.leave_offset= #{leaveOffset}

where 1=1

    and t.classes_id= #{classesId}

