mapper.select:
select
count(1) count
from s_community t
left join city_area ca on t.city_code = ca.area_code and ca.status_cd = '0'
left join city_area ca1 on ca1.area_code = ca.parent_area_code and ca.status_cd = '0'
where 1=1

    and t.community_id = #{communityId}


    and t.name = #{name}


    and t.city_code = #{cityCode}


    and t.ext_community_id = #{extCommunityId}


    and t.app_id = #{appId}

and t.status_cd = '0'

select t.community_id communityId, t.name , t.address, t.city_code cityCode, t.create_time createTime, t.ext_community_id extCommunityId, t.app_id appId, ca.area_name areaName, ca.parent_area_name cityName, ca1.parent_area_name provName from s_community t left join city_area ca on t.city_code = ca.area_code and ca.status_cd = '0' left join city_area ca1 on ca1.area_code = ca.parent_area_code and ca.status_cd = '0' where 1=1
and t.community_id = #{communityId}
and t.name = #{name}
and t.city_code = #{cityCode}
and t.ext_community_id = #{extCommunityId}
and t.app_id = #{appId}
and t.status_cd = '0' order by t.create_time desc
limit #{page}, #{row}

mapper.update:
update s_community t
t.community_id = #{communityId},
t.name = #{name},
t.address = #{address},
t.city_code = #{cityCode},
t.status_cd = #{statusCd},
t.ext_community_id = #{extCommunityId}
where 1=1 and t.status_cd = '0'
and t.community_id = #{communityId}
and t.app_id = #{appId}
