mapper.select:
SELECT t.log_id logId, t.machine_id machineId, t.machine_type_cd machineTypeCd, t.operate_type operateType, t.user_id userId, t.user_name userName, t.req_param reqParam, t.res_param resParam, t.state, t.create_time createTime, td1.name operateTypeName, m.machine_code machineCode, m.machine_name machineName, m.machine_ip machineIp, td2.`name` stateName FROM operate_log t INNER JOIN machine m ON t.machine_id = m.machine_id AND m.status_cd = '0' LEFT JOIN t_dict td1 ON t.operate_type = td1.status_cd AND td1.table_name = 'operate_log' AND td1.table_columns = 'operate_type' LEFT JOIN t_dict td2 ON t.state = td2.status_cd AND td2.table_name = 'operate_log' AND td2.table_columns = 'state' where 1=1
and t.log_id = #{logId}
and t.machine_id = #{machineId}
and m.machine_code = #{machineCode}
and m.machine_ip = #{machineIp}
and m.machine_name = #{machineName}
and t.machine_type_cd = #{machineTypeCd}
and t.operate_type = #{operateType}
and t.user_id = #{userId}
and t.userName = #{userName}
and t.state = #{state}
order by t.create_time desc
limit #{page}, #{row}

select
count(1) count
FROM
operate_log t
INNER JOIN machine m ON t.machine_id = m.machine_id
AND m.status_cd = '0'
LEFT JOIN t_dict td1 ON t.operate_type = td1.status_cd
AND td1.table_name = 'operate_log'
AND td1.table_columns = 'operate_type'
LEFT JOIN t_dict td2 ON t.state = td2.status_cd
AND td2.table_name = 'operate_log'
AND td2.table_columns = 'state'
where 1=1

    and t.log_id = #{logId}


    and t.machine_id = #{machineId}


    and m.machine_code = #{machineCode}


    and m.machine_ip = #{machineIp}


    and m.machine_name = #{machineName}


    and t.machine_type_cd = #{machineTypeCd}


    and t.operate_type = #{operateType}


    and t.user_id = #{userId}


    and t.userName = #{userName}


    and t.state = #{state}
