mapper.select:
SELECT t.hm_id hmId, t.hm_name hmName, t.version, t.protocol_impl protocolImpl, t.hm_type hmType, t.author, t.link, t.license, t.prod_url prodUrl, t.create_time createTime, t.default_protocol defaultProtocol FROM hardware_manufacturer t WHERE 1 = 1
AND t.hm_id = #{hmId}
AND t.hm_name = #{hmName}
AND t.version = #{version}
AND t.protocol_impl = #{protocolImpl}
AND t.hm_type = #{hmType}
AND t.default_protocol = #{defaultProtocol}
AND t.status_cd = '0' order by t.create_time desc
limit #{page}, #{row}

select
count(1) count
FROM
hardware_manufacturer t
WHERE
1 = 1

    AND t.hm_id = #{hmId}


    AND t.hm_name = #{hmName}


    AND t.version = #{version}


    AND t.protocol_impl = #{protocolImpl}


    AND t.hm_type = #{hmType}


    AND t.default_protocol = #{defaultProtocol}

AND t.status_cd = '0'

mapper.update:
update hardware_manufacturer t
set t.default_protocol = #{defaultProtocol}
where
1=1

    AND t.hm_type = #{hmType}


    AND t.hm_id = #{hmId}

AND t.status_cd = '0'

mapper.select:
SELECT t.hm_id hmId, t.attr_id attrId, t.spec_cd specCd, t.value value, hm.hm_name hmName, hm.version, hm.protocol_impl protocolImpl, hm.hm_type hmType, hm.author, hm.link, hm.license, hm.prod_url prodUrl, hm.create_time createTime, hm.default_protocol defaultProtocol FROM hardware_manufacturer_attr t LEFT JOIN hardware_manufacturer hm on t.hm_id = hm.hm_id WHERE 1 = 1
AND t.hm_id = #{hmId}
AND t.spec_cd = #{specCd}
AND t.attr_id = #{attrId}
AND t.value = #{value}
AND t.status_cd = '0' AND hm.status_cd = '0' order by t.create_time desc
