mapper.select:
SELECT t.tran_id tranId, m.machine_id machineId, t.url url, t.req_header reqHeader, t.req_param reqParam, t.res_header resHeader, t.res_param resParam, t.req_time reqTime, t.res_time resTime, t.create_time createTime, t.machine_code machineCode, m.machine_name machineName, m.machine_ip machineIp FROM transaction_log t left JOIN machine m ON t.machine_code = m.machine_code AND m.status_cd = '0' where 1=1
and t.tran_id = #{tranId}
and m.machine_id = #{machineId}
and t.machine_code = #{machineCode}
and m.machine_name = #{machineName}
and t.url = #{url}
order by t.create_time desc
limit #{page}, #{row}

select
count(1) count
FROM
transaction_log t
left JOIN machine m ON t.machine_code = m.machine_code
AND m.status_cd = '0'
where 1=1

    and t.tran_id = #{tranId}


    and m.machine_id = #{machineId}


    and t.machine_code = #{machineCode}


    and m.machine_name = #{machineName}


    and t.url = #{url}
