mapper.select:
select t.log_id logId, t.staff_id staffId, t.staff_name staffName, t.ext_staff_id extStaffId, t.machine_id machineId, t.machine_name machineName, t.ext_machine_id extMachineId, t.photo_jpg photoJpg, t.community_id communityId, m.direction from manual_open_door_log t left join machine m on m.machine_id = t.machine_id and m.status_cd = '0' where 1=1
and m.location_obj_id = #{boxId}
and t.staff_id = #{staffId}
and t.staff_name like concat('%',#{staffName},'%')
and t.ext_staff_id = #{extStaffId}
and t.community_id = #{communityId}
and t.ext_machine_id = #{extMachineId}
order by t.create_time desc
limit #{page}, #{row}

select
count(1) count
from manual_open_door_log t
left join machine m on m.machine_id = t.machine_id and m.status_cd = '0'
where 1=1

    and m.location_obj_id = #{boxId}


    and t.staff_id = #{staffId}


    and t.staff_name like concat('%',#{staffName},'%')


    and t.ext_staff_id = #{extStaffId}


    and t.community_id = #{communityId}


    and t.ext_machine_id = #{extMachineId}
