mapper.update:
update machine_attrs t
set t.value = #{value}
where 1=1

    and t.machine_id = #{machineId}


    and t.community_id = #{communityId}


    and t.attr_id = #{attrId}


    and t.spec_cd = #{specCd}

and t.status_cd = '0'
