mapper.select:
select
count(1) count
from parking_coupon_car t
where 1=1

    and t.pcc_id = #{pccId}


    and t.coupon_name = #{couponName}


    and t.community_id = #{communityId}


    and t.ext_pcc_id = #{extPccId}


    and t.pa_id = #{paId}


    and t.car_num = #{carNum}


    and t.give_way = #{giveWay}


    and t.type_cd = #{typeCd}


    and t.value = #{value}


    and t.state = #{state}


    and t.remark = #{remark}


    and t.start_time = #{startTime}


    and t.end_time = #{endTime}


    and t.ext_pcc_id = #{extPccId}


and t.status_cd = '0'

select
t.pcc_id pccId,
t.coupon_name couponName,
t.shop_name shopName,
t.community_id communityId,
t.create_time createTime,
t.ext_pcc_id extPccId,
t.pa_id paId,
t.car_num carNum,
t.give_way giveWay,
t.type_cd typeCd,
t.value,
t.state,
t.remark,
t.start_time startTime,
t.end_time endTime,
t.create_time createTime,
t.ext_pcc_id extPccId
from parking_coupon_car t
where 1=1

    and t.pcc_id = #{pccId}


    and t.coupon_name = #{couponName}


    and t.community_id = #{communityId}


    and t.ext_pcc_id in

        #{item}



    and t.pa_id = #{paId}


    and t.car_num = #{carNum}


    and t.give_way = #{giveWay}


    and t.type_cd = #{typeCd}


    and t.value = #{value}


    and t.state = #{state}


    and t.remark = #{remark}


    and t.start_time = #{startTime}


    and t.end_time = #{endTime}


    and t.ext_pcc_id = #{extPccId}

and t.status_cd = '0'
order by t.create_time desc

    limit #{page}, #{row}

